function AddTool(n){var t;switch(n){case"text":t=$("<div>").addClass("comp-text").text("sample");break;case"circle":t=$("<div>").addClass("comp-circle")}t.addClass("comp").draggable({start:function(){$(".comp.selected").removeClass("selected");$(this).addClass("selected")}});$(".draw-content").append(t)}const connection=(new signalR.HubConnectionBuilder).withUrl("/commanding").configureLogging(signalR.LogLevel.Information).build();connection.on("commanded",function(n,t){console.log("socket sent:",n,t);switch(n){case"Color":$(".comp.selected").css("background-color",t)}});connection.start();